function ReplaceTag(e,t,n){this.str=e;this.tag=t;if(n===false){this.replaceAll=false}else{this.replaceAll=true}this.getStartIndex=function(e){var t="<"+this.tag;e=this.str.indexOf(t,e?e:0);while(true){if(e===-1){break}var n=this.str.charAt(e+t.length);if(n==" "||n==">"){return e}e=this.str.indexOf(t,e+1)}return-1};this.getEndIndex=function(e){var t="</"+this.tag;var n=this.str.indexOf(t,e);while(true){if(n===-1){break}var r=this.str.charAt(n+t.length);if(r==" "||r==">"){return n}n=this.str.indexOf(t,n+1)}return-1};this.initialize=function(){var e=this.getStartIndex();while(true){if(e===-1){break}var t=this.getEndIndex(e);t=this.str.indexOf(">",t);t++;var n=this.str.substr(0,e);n+=this.str.substr(t);this.str=n;if(!this.replaceAll){break}e=this.getStartIndex()}return this.str};return this.initialize()}
